#include <stdio.h>

void  main()
{
    int tick = 0; //0~86399
    short mouth[12] = {31,29,31,30,31,30,31,31,30,31,30,31}; 
    short date; 
    short yue;
    short week; 
    char wday[][7] = {"星期一","星期二","星期三","星期四","星期五","星期六","星期天"};
