	}
	if(byte == 4)
	{
		led4 = ~led4;  // 切换 LED4 状态
	}
}

void updatehander(char* str)
{
	while(*str)
	{
		uarthander(*str++);  // 逐字符发送字符串到串口
	}
}

void handergo() interrupt 0
{
	updatehander("Hello,World\n");  // 外部中断0触发时发送 "Hello,World\n" 到串口
