unsigned char j = 0;
static char w = '0';

void abcd()
{
	a = sib[0] - '0';  // 将字符转换为数字
	b = sib[1] - '0';  // 将字符转换为数字
	c = sib[2] - '0';  // 将字符转换为数字
	d = sib[3] - '0';  // 将字符转换为数字
}

void comhander() interrupt 4
{
	w = SBUF;  // 从串口缓冲区读取接收到的字符
	sib[j++] = w;  // 存储接收到的字符

	if(j >= 4)
