```c
void putdata(unsigned char str) 
{
    int i = 8;
    str = str + str;//由于波特率误差，发送的AscII码会小一半所以我直接加一倍
    TR1 = 1;
    TXD2 = 1;
    waittf();
    while (i--)
    {
        TXD2 = str & 0x01;
        waittf();
        str >>= 1;
    }
    TXD2 = 0;
    waittf();
    TR1 = 0;
}

void putdatastr(unsigned char *str)
{
    while (*str)
    {
        putdata(*str);  // 发送字符串中的每个字符
        str++;
    }
}
